    nt-PA, 1.2 nM with 0.8 mM HDIleProArgpNA;
    urokinase, 0.4 nM with 0.4 mM pyroGluGlyArgpNA;
    aPC, 3 nM with 0.174 mM pyroGluProArgpNA;
